mapper.select:
SELECT mb.email, mbb.accno AS accno, mbb.mem_bank_id as memBankId, mbb.user_name AS userName, pb.bank_name AS bankName, mbb.bank_card_no AS bankCardNo, mbb.`status` AS `status`, mbb.create_time AS createTime FROM mem_bank mbb INNER JOIN mem_baseinfo mb ON mb.accno = mbb.accno INNER JOIN pay_bank pb ON mbb.bank_id = pb.bank_id
and mb.email = #{req.email}
and mbb.bank_card_no = #{req.bankCardNo}
and mbb.create_time >= #{req.startTime}
and mbb.create_time <= #{req.endTime}
ORDER BY mbb.create_time desc

SELECT * FROM tra_applycash t WHERE 1=1
and t.accno = #{accno}

    AND t.apycstatus in

        #{s}


limit 1
