SELECT mb.email, mw.accno AS accno, mbb.waller_id as wallerId, mbb.money_address AS moneyAddress, mbb.`status` AS `status`, mbb.create_time AS createTime FROM mem_waller mw INNER JOIN mem_baseinfo mb ON mb.accno = mbb.accno
and mb.email = #{req.email}
and mw.money_address = #{req.moneyAddress}
and mw.create_time >= #{req.startTime}
and mw.create_time <= #{req.endTime}
ORDER BY mw.create_time desc

SELECT * FROM tra_applycash t WHERE 1=1
and t.accno = #{accno}

    AND t.apycstatus in

        #{s}


